body	{background:#ffffff; font-family:monospace, sans-serif; font-size:16px; line-height:24px; color:#444; width:100%; padding:0px;}
h1		{font-size:4em; font-weight:normal; color:#1400ff;}
h2		{font-size:2em; font-weight:normal;}

a		{color:#1400ff;text-decoration:none;}

/*
a.bar	{color:#f7f4fd; background-color:#3a3a3a; border-radius:4px; padding:5px; text-decoration:none; border-bottom:none;}
a.bar:hover	{background-color:#1400ff; border-bottom:none;}
*/

.nav	{display:inline-block; vertical-align:top; width:50%;}
.nav:nth-of-type(2)	{text-align:right;}

.line, .blog_wrap	{border-top:1px dashed #1400ff; margin-top:40px; margin-bottom:40px; position:relative;}
.blog_info	{position:absolute; right:10px; top:10px;}


#test div { margin:16px; }

.arss_page
{
	position:absolute;
	left:0px;
	width:75%;
	height:100%;
	overflow:scroll;
}

#arss_butt_drag { width:16px; cursor: move; }
#arss_butt_read { width:64px; cursor: pointer; }
#arss_butt_feed { width:64px; cursor: pointer; }
#arss_butt_opts { width:64px; cursor: pointer; }
#arss_butt_status { text-align:left; }
.arss_butt
{
	line-height:32px;
	height:32px;
	display:inline-block;
	text-align:center;
	background:#f0f0f0;
}
.arss_butt:hover
{
	background:#e0e0e0;
}

#arss_info_butt_load_opml { cursor: inherit; }
.arss_info_butt
{
	line-height:32px;
	display:block;
	text-align:center;
	background:#f0f0f0;
	width:90%;
	margin: 32px auto ;
	cursor: pointer;
}
.arss_info_butt:hover
{
	background:#e0e0e0;
}
.arss_info_butt_info
{
	width:90%;
	margin: 32px auto ;
}
.arss_info_butt_warn
{
	width:100%;
	padding: 32px 5% ;
	background:#ffc0c0;
}


.arss_bar
{
	position:absolute;
	right:0px;
	left:75%;
	top: 0px;
	height:32px;
	overflow:hidden;
	background:#f0f0f0;
}

.arss_list_read
{
	width: 100%;
}
.arss_list_feed
{
	width: 100%;
}
.arss_list
{
	position:absolute;
	right:0px;
	left:75%;
	top: 32px;
	bottom:0px;
	overflow-x:hidden;
	overflow-y:scroll;
}

.arss_item_link
{
}

.arss_item_date
{
  text-align: right;
	cursor: pointer;
}

.arss_item_feed
{
	cursor: pointer;
	text-align:right;
	padding:0px 0px 0px 50%;
	width: 100%;
	font-size:8px;
	line-height:16px;
}
.arss_item
{
	padding:16px 8px;
	width: 100%;
}

.arss_info
{
	padding:16px 8px;
	width: 100%;
	background:#ffffff;
}

.arss_feed
{
	padding:16px 8px;
	width: 100%;
}

.arss_feed input[type=checkbox]
{
	display:inline-block;
	width:32px;
}

.arss_feed input[type=text]
{
	font-size:24px;
	width:100%;
}

.arss_feed_select
{
	cursor: pointer;
}

.arss_feed_delete
{
	cursor: pointer;
}

.active
{
	background:#f0f0f0;
}
